import React,{useState} from 'react'
import './ExpenseForm.css'
const ExpenseForm = () => {
    const [
        textInput, setTextInput
    ] = useState('')
    const [
        numberAmount, setNumberAmount
    ] = useState('')
    const [
        dates, setDates
    ] = useState('')

    const handlerTextInput = e => {
        console.log('INI HANDLER TITLE',e.target.value)

    }
    const handlerAmount = e => {
        console.log('INI HANDLER AMOUNT',e.target.value)
    }
    const handlerDates = e => {
        console.log('INI HANDLER DATES',e.target.value)
    }


    // HANDLER SUBMIT
    const handlerSubmit = e => {
        e.preventDefault();
        console.log('INI E BUTTON ->',e)
    }
    return (
        <form onSubmit={handlerSubmit}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>
                        Title
                    </label>
                    <input type="text" onChange={handlerTextInput}/>
                </div>
                <div className="new-expense__control">
                    <label>
                        Amount
                    </label>
                    <input type="number" min="0.01" step="0.01" onChange={handlerAmount} />
                </div>
                <div className="new-expense__control">
                    <label>
                        Date
                    </label>
                    <input type="date" min="2020-01-01" max="2025-12-31" onChange={handlerDates} />
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </form>
    )
}

export default ExpenseForm
