import React from "react"
import './ExpenseItem.css'
const ExpenseItem = (props) => {
    const months = props.dates.toLocaleString('id-ID', { month : 'long' })
    const days = props.dates.toLocaleString('id-ID', { day : '2-digit' })
    const years = props.dates.getFullYear()
    return (
        <div className="expense-item">
            <div className="">
                <div>{months}</div>
                <div>{years}</div>
                <div>{days}</div>
            </div>
            <div className="expense-item__description">
                <h2>{props.title}</h2>
                <div className="expense-item__price">${props.amount}</div>
            </div>
        </div>
    )
}

export default ExpenseItem
