// STYLING
import './ExpenseDate.css'

function ExpenseDate({date}) {
    const getMonths = date.toLocaleString('id-ID', {month: 'long' })
    const getDates = date.toLocaleString('id-ID', { day: '2-digit' })
    const getYearsNow = date.getFullYear()
    return (
        <div className="expense-date">
            <div className="expense-date__month">{getMonths}</div>
            <div className="expense-date__year">{getYearsNow}</div>
            <div className="expense-date__day">{getDates}</div>
        </div>
    )
}

export default ExpenseDate
