import React,{useState} from "react"
import './ExpenseForm.css'
const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDates, setEnteredDates] = useState('')


    // Handler title 
    const handlerTitle = e => {
        console.log('INI HANDLER TITLE', e.target.value)
        e.preventDefault()
        setEnteredTitle(e.target.value)
    }
    // Handler Amount
    const handlerAmount = e => {
        e.preventDefault()
        setEnteredAmount(e.target.value)
    }
    // Handler Date
    const handlerDate = e => {
        e.preventDefault()
        setEnteredDates(e.target.value)
    }
    // HandlerSubmit
    const handlerSubmit = e => {
        console.log('EVEN',e)
        e.preventDefault()
        setTimeout(() => {
            console.log('Helo')
        }, 2000)
    }

    return (
        <form onSubmit={handlerSubmit}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={handlerTitle}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={handlerAmount}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" step="2022-12-31" onChange={handlerDate}/>
                </div>
                </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>
        </form>
    )
}

export default ExpenseForm
