import React from 'react'
import '../Expenses/ExpenseDate.css'
const ExpenseDate = (props) => {
    var options = {
        weekday: "short",
        year: "numeric",
        month: "2-digit",
        day: "numeric"
     };

    let months = props.dates.toLocaleDateString('en-US', options)
    console.log(months)
    let days = props.dates.toLocaleDateString('en-US', options)
    console.log(days)
    let years = props.dates.getFullYear()
    return (
        <div className="expense-date">
                <div className="expense-date__month">{months}</div>
                <div className="expense-date__year">{years}</div>
                <div className="expense-date__day">{days}</div>
        </div>
    )
}

export default ExpenseDate
