import React, {useState} from 'react'
import ExpenseItem from './ExpenseItem'
import ExpensesFilter from './ExpensesFilter'
import './Expences.css'
import Card from '../UI/Card'

const Expences = (props) => {
    const {
        items
    } = props
    // Usetate
    const [
      filteredYears, setFilteredYears
    ] = useState('2021')
    // MAKE PROPS AND PASSING 
    const filterChangeHandler = (selectedYears) => {
      console.log('INI TAHUN',selectedYears)
      setFilteredYears(selectedYears)
    }
    return (
      <div>
        <Card className="expenses">
          <ExpensesFilter selected={filteredYears} onChangeFilter={filterChangeHandler}/>
          {
            props.items.map(res => 
              <ExpenseItem 
                key={res.id}
                title={res.title}
                amount={res.amount}
                dates={res.date}
              />   
            )
          }
        </Card>
      </div>
    )
}
export default Expences
