import React,{useState} from "react"
import './ExpenseForm.css'
const ExpenseForm = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDates, setEnteredDates] = useState('')
    // Handler title 
    const handlerTitle = e => {
        console.log('INI HANDLER TITLE', e.target.value)
        setEnteredTitle(e.target.value)
    }
    // Handler Amount
    const handlerAmount = e => {
        console.log('INI HANDLER AMOUNT', e.target.value)
        setEnteredAmount(e.target.value)
    }
    // Handler Date
    const handlerDate = e => {
        console.log('INI HANDLER DATE', e.target.value)
        setEnteredDates(e.target.value)
    }
    // HandlerSubmit
    const handlerSubmit = e => {
        e.preventDefault()
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            // dates: new Date(enteredDates)
        }
        props.onSaveExpenseData(expenseData)
        setEnteredTitle('')
        setEnteredAmount('')
        setEnteredDates('')

    }

    return (
        <form onSubmit={handlerSubmit}>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input 
                        type="text" 
                        onChange={handlerTitle}
                        value={enteredTitle}
                    />
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input 
                        type="number" 
                        min="0.01" 
                        step="0.01" 
                        onChange={handlerAmount}
                        value={enteredAmount}
                    />
                </div>
                {/* <div className="new-expense__control">
                    <label>Date</label>
                    <input 
                        type="date" 
                        onChange={handlerDate}
                        value={enteredDates}
                    />
                </div> */}
                </div>
                <div className="new-expense__actions">
                    <button type="submit">Add Expense</button>
                </div>
        </form>
    )
}

export default ExpenseForm
