import './ExpenseItem.css'

function ExpenseItem({title, amount, date}) {
    const getMonths = date.toLocaleString('id-ID', {month: 'long' })
    const getDates = date.toLocaleString('id-ID', { date: '2-digit' })
    const getYearsNow = date.getFullYear()
    return (
        <div className="expense-item">
            <div>
                <div>{getMonths}</div>
                <div>{getYearsNow}</div>
                <div>{getDates}</div>
            </div>
            <div className="expense-item__description">
                <h2>{title}</h2>
                <div className="expense-item__price">${amount}</div>
            </div>
        </div>
    );
}

export default ExpenseItem
