import React,{useState} from "react";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
const Expense = (props) => {

  const [
    filteredYear, setFilteredYear
  ] = useState('2020')

  const filterChangedHandler = (data) => { 
    setFilteredYear(data)
  }

  const selectedFilterYears = props.items.filter((item) => item.date.getFullYear() === filteredYear)

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter onChangeFilter={filterChangedHandler} selectedYears={filteredYear}/>
        {
          selectedFilterYears.map((expense) => (
            console.log('INI EXPENSE',expense)
            // <ExpenseItem
            //   key={expense.id} 
            //   title={expense.title}
            //   amount={expense.amount}
            //   date={expense.amount}
            // />
          ))}
      </Card>
    </div>
  );
};

export default Expense;
