import React,{useState} from "react";
import Expences from "./components/Expenses/Expences";
import NewExpense  from "./components/NewExpense/NewExpense";

const DUMMY_LIST = [
  {
    id: 1,
    title: 'Car Insurance',
    amount: 299.30,
    date: new Date()
  },
  {
    id: 2,
    title: 'Buy MacBook',
    amount: 399.00,
    date: new Date()
  },
  {
    id: 3,
    title: 'Build PC',
    amount: 400.30,
    date: new Date()
  },
  {
    id: 4,
    title: 'Eat',
    amount: 20.00,
    date: new Date()
  },
]

const App = () => {
  const [
    expenses, setExpenses
  ] = useState(DUMMY_LIST)
  // UNTUK MENAMBAH DATA 
  const getDataFromChild = expense => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses]
    })
  }

  return (
    <div>
      <NewExpense getData={getDataFromChild} />
      <Expences items={expenses}/>
    </div>
  );
}

export default App;
