import React from 'react'
import '../Expenses/ExpenseDate.css'
const ExpenseDate = (props) => {
    const {
        dates
    } = props
    let months = dates.toLocaleDateString('en-US', { month : 'long' })
    console.log(months)
    let days = dates.toLocaleDateString('en-US', { day : '2-digit' })
    console.log(days)
    let years = dates.getFullYear()
    return (
        <div className="expense-date">
                <div className="expense-date__month">{months}</div>
                <div className="expense-date__year">{years}</div>
                <div className="expense-date__day">{days}</div>
        </div>
    )
}

export default ExpenseDate
