import React from "react";
import Expences from "./components/Expenses/Expences";
import NewExpense  from "./components/NewExpense/NewExpense";
const App = () => {

  // UNTUK MENAMBAH DATA 
  const getDataFromChild = (data) => {
    console.log('INI DATA BRAY',data)
    const {
      id, amount, dates, title
    } = data
    expense.push({
      id: id,
      amount: amount,
      dates: dates,
      title: title
    })
  }
  const expense = [
    {
      id: 1,
      title: 'Car Insurance',
      amount: 299.30,
      date: new Date(2021, 3, 28)
    },
    {
      id: 2,
      title: 'Buy MacBook',
      amount: 399.00,
      date: new Date(2021, 5, 15)
    },
    {
      id: 3,
      title: 'Build PC',
      amount: 400.30,
      date: new Date(2021, 5, 10)
    },
    {
      id: 4,
      title: 'Eat',
      amount: 20.00,
      date: new Date(2021, 10, 20)
    },
  ]
  console.log(expense)
  return (
    <div>
      <NewExpense getData={getDataFromChild} />
      <Expences items={expense}/>
    </div>
  );
}

export default App;
